import { useEffect,useState } from "react";
import Products from "./components/Products.js";
import CartItems from "./components/CartItems.js";
import "./App.css";
import * as product from './store/product';
import * as shoppingCart from './store/cart';
import { useDispatch, useSelector } from "react-redux";



const App = (props) =>  {

  const dispatch = useDispatch();
  const [msg, setMsg] = useState("");
  const products = useSelector((state) => state.entities.products.list);
  const cartItems = useSelector((state) => state.entities.carts.list);
  //const [cartItems, setCartItems] = useState([]);

  const loadProducts = async () => {
    //const res = await fetch("http://localhost:5000/api/products", { mode: 'no-cors' });
    const res = await fetch("http://localhost:5000/api/products");
    const pro = await res.json();

    console.log('get from api=>',pro);
    dispatch(product.fetched(pro));
    //setProducts(pro);
  };

  const loadCarts = async () => {
    //const res = await fetch("http://localhost:5000/api/products", { mode: 'no-cors' });
    const res = await fetch("http://localhost:5000/api/carts");
    const cart = await res.json();

    console.log('get from api=>', cart);
    dispatch(shoppingCart.fetched(cart));
    //setProducts(pro);
  };

  useEffect(() => {
    loadProducts();
    loadCarts();
  }, []);

  const removeCart = async (id) => {

    try{
      const res = await fetch("http://localhost:5000/api/carts/"+id,{method: "DELETE"});
      const output = await res.json();
      //setMsg(output);
      dispatch(shoppingCart.removed({id}));
    }catch (err) {
      console.error(err);
    }




    //cartItems_clone = cartItems_clone.filter((cart)=> cart.id !== id);
    //setCartItems(cartItems_clone.filter((cart) => cart.id !== id));
    //console.log("remove cart", id);
  };

  const checkout = (user) => {
    console.log(user);
    alert("checkout inviato con successo");
  };



  const addCart = (product) => {
    dispatch(shoppingCart.added({product}));
  };

console.log("productslist=>", products)

return (

  <div className="app">
        <header></header>
        <main>
          <div className="main">
            <Products data={products} addCart={addCart} />
          </div>
          <div className="sidebar">
            {msg}
            <CartItems
              cartItems={cartItems}
              removeCart={removeCart}
              checkout={checkout}
            />
          </div>
        </main>
        <footer>footer</footer>
      </div>
  );
}

export default App;
